import React, { useState } from 'react';

function App() {
  const [transactions, setTransactions] = useState([
    { id: 1, category: 'Food', description: 'Lunch', amount: 15.5 },
    { id: 2, category: 'Shopping', description: 'Groceries', amount: 30.0 },
    { id: 3, category: 'Entertainment', description: 'Movie tickets', amount: 20.0 },
  ]);

  const [newTransaction, setNewTransaction] = useState({
    category: '',
    description: '',
    amount: '',
  });

  const [searchTerm, setSearchTerm] = useState('');

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const newTransactionId = Math.max.apply(
      Math,
      transactions.map((transaction) => transaction.id)
    ) + 1;
    const newTransactionObj = {
      id: newTransactionId,
      category: newTransaction.category,
      description: newTransaction.description,
      amount: newTransaction.amount,
    };
    setTransactions([...transactions, newTransactionObj]);
    setNewTransaction({
      category: '',
      description: '',
      amount: '',
    });
  };

  const filteredTransactions = transactions.filter((transaction) =>
    transaction.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="App">
      <h1>Bank Transactions</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Category:
          <input
            type="text"
            value={newTransaction.category}
            onChange={(event) =>
              setNewTransaction({
                ...newTransaction,
                category: event.target.value,
              })
            }
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            value={newTransaction.description}
            onChange={(event) =>
              setNewTransaction({
                ...newTransaction,
                description: event.target.value,
              })
            }
          />
        </label>
        <label>
          Amount:
          <input
            type="number"
            value={newTransaction.amount}
            onChange={(event) =>
              setNewTransaction({
                ...newTransaction,
                amount: event.target.value,
              })
            }
          />
        </label>
        <button type="submit">Add Transaction</button>
      </form>
      <input
        type="text"
        value={searchTerm}
        onChange={handleInputChange}
        placeholder="Search transactions..."
      />
      <table>
        <thead>
          <tr>
            <th>Category</th>
            <th>Description</th>
            <th>Amount</th>
          </tr>
        </thead>
        <tbody>
          {filteredTransactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.category}</td>
              <td>{transaction.description}</td>
              <td>{transaction.amount}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
